const _=require('lodash-core');
class Engine{
	constructor(inputDocument, params, outputDocument){
		let inputKey = 'documentIn';
		if(Array.isArray(inputDocument)){
			inputKey = 'documentsIn';
		}
		this['sensei:inputs'] = {};
		this['sensei:inputs'][inputKey] = inputDocument;
		this['sensei:params'] =  _.cloneDeep(params);
		this['sensei:outputs'] = {'documentOut': outputDocument};
	}

	set executionInfo(executionInfo){
		this['sensei:executionInfo'] = executionInfo;
	}
	set inputs(inputs){
		this['sensei:inputs'] = inputs;
	}
	set params(params){
		this['sensei:params'] = Object.assign({},params);
	}
	set outputs(outputs){
		this['sensei:outputs'] = outputs;
	}
}

module.exports = Engine;
